pragma solidity ^0.5.0;

import "./KIP7.sol";
import "./OwnerRole.sol";
import "./KIP13.sol";

/**
 * @dev Extension of `KIP7` that adds a set of accounts with the `OwnerRole`,
 * which have permission to mint (create) new tokens as they see fit.
 *
 * At construction, the deployer of the contract is the only minter.
 *
 * See http://kips.klaytn.com/KIPs/kip-7-fungible_token
 */
contract SigmoidToken is KIP13, KIP7, OwnerRole {
    using SafeMath for uint256;
    using Address for address;
    /*
     *     bytes4(keccak256('mint(address,uint256)')) == 0x40c10f19
     *     bytes4(keccak256('isOwner(address)')) == 0xaa271e1a
     *     bytes4(keccak256('addOwner(address)')) == 0x983b2d56
     *     bytes4(keccak256('renounceOwner()')) == 0x98650275
     *
     *     => 0x40c10f19 ^ 0xaa271e1a ^ 0x983b2d56 ^ 0x98650275 == 0xeab83e20
     */
    bytes4 private constant _INTERFACE_ID_KIP7MINTABLE = 0xeab83e20;

    // Token symbol name
    string public symbol;
    string public name;

    // lock initialization
    bool locked = false;

    uint8 public decimals;

    uint public epochCount;             // number of 'blocks' mined
    uint public latestDifficultyPeriodStarted;
    uint public rewardEra;              // era for halving reward
    uint public maxSupplyForEra;        // maximum supply that can be minted in current era
    uint public maxTotalSupply;         // total maximum supply that can be minted

    uint public currentTarget;          // hash should be lower than this
    bytes32 public previousBlockHash;   // recent Klaytn block hash. used to prevent pre-hashing
    // nonces already used for minting. prevents mining with the same answer
    mapping(bytes32 => bytes32) solutionForChallenge;

    // for checking
    address public lastRewardTo;
    uint public lastRewardAmount;
    uint public lastRewardEthBlockNumber;

    // parameters for difficulty adjustment
    uint public _BLOCKS_PER_READJUSTMENT = 64;    // difficulty re-adjustment 'block' interval
    uint public _MINIMUM_TARGET = 2 ** 16;
    uint public _MAXIMUM_TARGET = 2 ** 234;

    // parameters for balloon hashing
    bool private is_balloon = true;
    uint private _S_COST = 256;   // make a 8kb scratchpad
    uint private _T_COST = 3;
    uint private _DELTA = 3;

    event Mint(address indexed from, uint reward_amount, uint epochCount, bytes32 newBlockHash);
    event Switch(bool is_balloon);

    constructor () public {
        // register the supported interfaces to conform to KIP7 via KIP13
        _registerInterface(_INTERFACE_ID_KIP7MINTABLE);

        symbol = "SIG";
        name = "Sigmoid Token";

        decimals = 8;
        maxTotalSupply = 21000000 * (10 ** uint(decimals));

        if(locked) revert();
        locked = true;

        epochCount = 1;
        latestDifficultyPeriodStarted = block.number;
        rewardEra = 0;
        maxSupplyForEra = maxTotalSupply.div(2);

        currentTarget = _MAXIMUM_TARGET;
        previousBlockHash = blockhash(block.number - 1);
    }

    // if hashing takes too much gas or hashing is way too easy, this function will be used
    function _fixHashingComplexity(uint s_cost, uint t_cost, uint delta) public onlyOwner {
        _S_COST = s_cost;
        _T_COST = t_cost;
        _DELTA = delta;
    }

    // if hashing takes WAY too much gas, give up balloon hashing
    function _giveupBalloonHashing() public onlyOwner {
        is_balloon = false;
        emit Switch(false);
    }

    // retry using balloon hashing
    function _retryBalloonHashing() public onlyOwner {
        is_balloon = true;
        emit Switch(true);
    }

    function mint(uint256 nonce) public returns (bool) {
        //the PoW must contain work that includes a recent Klaytn block hash and the msg.sender's address to prevent MITM attacks
        bytes32 digest;
        if (is_balloon) {
            digest = _balloon_hash(nonce, _S_COST, _T_COST, _DELTA);
        } else {
            digest = keccak256(abi.encodePacked(previousBlockHash, msg.sender, nonce));
        }

        //the digest must be smaller than the target
        if(uint256(digest) > currentTarget) revert();

        //only allow one reward for each challenge
        bytes32 solution = solutionForChallenge[previousBlockHash];
        solutionForChallenge[previousBlockHash] = digest;
        if(solution != 0x0) revert();  //prevent the same answer from awarding twice

        // rewarding
        uint reward_amount = getMiningReward();
        _mint(msg.sender, reward_amount);

        // cannot mint more tokens than the assigned amount to the current era
        assert(totalSupply() <= maxSupplyForEra);

        //set readonly diagnostics data
        lastRewardTo = msg.sender;
        lastRewardAmount = reward_amount;
        lastRewardEthBlockNumber = block.number;

        _startNewMiningEpoch();
        emit Mint(msg.sender, reward_amount, epochCount, previousBlockHash);
        return true;
    }

    // Sigmoid: Balloon hashing.
    function _balloon_hash(uint256 nonce, uint s_cost, uint t_cost, uint delta) internal returns (bytes32 digest) {
        bytes32[] memory buf = new bytes32[](s_cost);
        uint cnt = 0;

        // Step 1. Expand input into buffer.
        buf[0] = sha256(abi.encodePacked(cnt++, previousBlockHash, msg.sender, nonce));
        for (uint m = 1; m < s_cost; m++) {
            buf[m] = sha256(abi.encodePacked(cnt++, buf[m - 1]));
        }

        // Step 2. Mix buffer contents.
        for (uint t = 0; t < t_cost; t++) {
            for (uint m = 0; m < s_cost; m++) {
                // Step 2a. Hash last and current blocks.
                buf[m] = (m == 0) ? sha256(abi.encodePacked(cnt++, buf[s_cost - 1], buf[0])) : sha256(abi.encodePacked(cnt++, buf[m - 1], buf[m]));

                // Step 2b. Hash in pseudorandomly chosen blocks.
                for (uint i = 0; i < delta; i++) {
                    uint other = uint256(sha256(abi.encodePacked(cnt++, previousBlockHash, msg.sender, sha256(abi.encodePacked(t, m, i))))) % s_cost; // in "big" order
                    buf[m] = sha256(abi.encodePacked(cnt++, buf[m], buf[other]));
                }
            }
        }

        // Step 3. Extract output from buffer.
        return buf[s_cost - 1];
    }

    function _startNewMiningEpoch() internal{
        // if max supply for the era will be exceeded next reward round, then enter the new era before that happens
        // 40 is the final reward era, almost all tokens minted
        // once the final era is reached, more tokens will not be given out because the assert function
        if(totalSupply().add(getMiningReward()) > maxSupplyForEra && rewardEra < 39)
            rewardEra = rewardEra.add(1);

        // set the next minted supply at which the era will change
        // total supply is 2100000000000000  because of 8 decimal places
        maxSupplyForEra = maxTotalSupply.sub(maxTotalSupply.div(2 ** (rewardEra + 1)));
        epochCount = epochCount.add(1);

        // re-adjust difficulty when the time comes
        if(epochCount % _BLOCKS_PER_READJUSTMENT == 0) _reAdjustDifficulty();

        // make the latest Klaytn block hash a part of the next challenge for PoW to prevent pre-mining future blocks
        // do this last since this is a protection mechanism in the mint() function
        previousBlockHash = blockhash(block.number - 1);
    }

    function _reAdjustDifficulty() internal {
        // Digishield implementation. Dogecoin version.
        // Klaytn: 1 block generation per second
        // Sigmoid: 1 minting per 10 minute
        uint modulatedTimespan = block.number - latestDifficultyPeriodStarted;
        uint retargetTimespan = _BLOCKS_PER_READJUSTMENT * 600;
        uint maxTimespan = retargetTimespan.add(retargetTimespan.div(2));
        uint minTimespan = retargetTimespan.sub(retargetTimespan.div(4));

        if (modulatedTimespan > retargetTimespan) {
            modulatedTimespan = retargetTimespan.add((modulatedTimespan.sub(retargetTimespan)).div(8));
        } else {
            modulatedTimespan = retargetTimespan.sub((retargetTimespan.sub(modulatedTimespan)).div(8));
        }

        // limit adjustment step
        if (modulatedTimespan < minTimespan) {
            modulatedTimespan = minTimespan;
        } else if (modulatedTimespan > maxTimespan) {
            modulatedTimespan = maxTimespan;
        }

        // retarget
        currentTarget = (currentTarget.mul(modulatedTimespan)).div(retargetTimespan);
        if (currentTarget < _MINIMUM_TARGET) {
            currentTarget = _MINIMUM_TARGET;
        } else if (currentTarget > _MAXIMUM_TARGET) {
            currentTarget = _MAXIMUM_TARGET;
        }

        latestDifficultyPeriodStarted = block.number;
    }

    // 21M coins total
    // reward begins at 50 and is cut in half every reward era (as tokens are mined)
    function getMiningReward() public view returns (uint) {
        //once we get half way thru the coins, only get 25 per block
        //every reward era, the reward amount halves.
        return (50 * (10 ** uint(decimals))).div(2**rewardEra) ;
    }

    // this is a recent Klaytn block hash, used to prevent pre-mining future blocks
    function getPreviousBlockHash() public view returns (bytes32) {return previousBlockHash;}

    // the number of zeroes the digest of the PoW solution requires. Auto adjusts
    function getMiningDifficulty() public view returns (uint) {return _MAXIMUM_TARGET.div(currentTarget);}

    function getCurrentTarget() public view returns (uint) {return currentTarget;}

    function getHashingParameters() public view returns (uint[3] memory) {return [_S_COST, _T_COST, _DELTA];}

    function isBalloon() public view returns (bool) {return is_balloon;}

    // Don't accept native asset
    function () external payable {revert();}

    // --------------------------------------
    // For Debugging
    // --------------------------------------

    function getMintDigest(uint256 nonce, uint256 salt, uint s_cost, uint t_cost, uint delta) public view returns (uint) {
        bytes32[] memory buf = new bytes32[](s_cost);
        uint cnt = 0;

        // Step 1. Expand input into buffer.
        buf[0] = sha256(abi.encodePacked(cnt++, salt, nonce));
        for (uint m = 1; m < s_cost; m++) {
            buf[m] = sha256(abi.encodePacked(cnt++, buf[m - 1]));
        }

        // Step 2. Mix buffer contents.
        for (uint t = 0; t < t_cost; t++) {
            for (uint m = 0; m < s_cost; m++) {
                // Step 2a. Hash last and current blocks.
                buf[m] = (m == 0) ? sha256(abi.encodePacked(cnt++, buf[s_cost - 1], buf[0])) : sha256(abi.encodePacked(cnt++, buf[m - 1], buf[m]));

                // Step 2b. Hash in pseudorandomly chosen blocks.
                for (uint i = 0; i < delta; i++) {
                    uint other = uint256(sha256(abi.encodePacked(cnt++, salt, sha256(abi.encodePacked(t, m, i))))) % s_cost; // in "big" order
                    buf[m] = sha256(abi.encodePacked(cnt++, buf[m], buf[other]));
                }
            }
        }

        // Step 3. Extract output from buffer.
        return uint256(buf[s_cost - 1]);
    }
}
